select base.gesture_name, base.p90 as baseline_90th, compare.p90 as compare_90th, base.avg as baseline_avg, compare.avg as compare_avg, (compare.p90 - base.p90) as difference_90th, (compare.avg - base.avg) as difference_avg, (compare.p90 - base.p90)/base.p90 * 100 as percent_change_90th, (compare.avg - base.avg)/base.avg * 100 as percent_change_avg FROM ( select  gesture_name, avg, p90 from gesture_summary where run="master-alm-nightly-performance-1537" and metric_name = "requestspan.spanresponsetimems" ) base join ( select  gesture_name, avg, p90 from gesture_summary where run="master-alm-nightly-performance-1538" and metric_name = "requestspan.spanresponsetimems" ) compare on ( base.gesture_name = compare.gesture_name ) where (compare.p90 - base.p90)/base.p90 * 100 > 20 and (compare.p90 - base.p90) > 50